import React from 'react';
import {Link} from 'react-router-dom';
import './Card.scss';

class Card extends React.Component {
  render () {
    const {kairos, match} = this.props;
    const id = match.params.id;
    console.log(kairos);
    return (
      <div className="main__directory--wrapper">
      {kairos
        .filter(item => item.title.includes(id) ? item : false)
        .map((item, index) => {
          return (
            <article className="directory__wrapper--card" key={index}>

                <div className="card__wrapper">
                  <div className="card__icon">
                    <i className="far fa-clock"></i>
                  </div>
                  <h3 className="card__title">
                  {item.title}
                  </h3>
                  <p className="card__description">
                  {item.description}
                  </p>
                </div>
                <Link className="main__directory--link" target="_blank" rel="noopener noreferrer" to={item.url}>{item.url}</Link>
          </article>

        )
      })}
      </div>
    )
  }
}

export default Card;
